import { useEffect, useRef, useState } from 'react';
import './Countdown.css';

const Countdown = () => {
  const [timerDays, setTimerDays] = useState('00');
  const [timerHours, setTimerHours] = useState('00');
  const [timerMinutes, setTimerMinutes] = useState('00');
  const [timerSeconds, setTimerSeconds] = useState('00');

  let interval = useRef();

  const startTimer = () => {
    const today = new Date ();
    const day = today.getDay() + '13'
    const hour = today.getHours( )
    // const countdown = new Date ('July 01, 2022 00:00:00')

  interval = setInterval(() => {
    // const now = ('13', '08', '12' , '05')
    const now = new Date().getTime();
    const distance = countdown - now;


    const days = Math.floor(distance / (1000 * 60 * 60 * 24));
    const hours = Math.floor((distance % (1000 * 60 * 60 * 24) / (1000 * 60 * 60)));
    const minutes = Math.floor((distance % (1000 * 60 * 60 )) / (1000 * 60));
    const seconds = Math.floor((distance % (1000 * 60)) / 1000);

    if (distance < 0) {
      clearInterval(interval.current);
    } else {
      setTimerDays(days);
      setTimerHours(hours);
      setTimerMinutes(minutes);
      setTimerSeconds(seconds);
    }

  }, 1000);
};   

  useEffect(() => {
    startTimer();
    return () => {
      clearInterval(interval.current);
    };
  })

  return (
    <section className="countdown">
      <h1 className='title'>LEEZY BOOST 103</h1>
      <section className='countdown-timer'>
      <span>{timerDays}</span>
      <span>{timerHours}</span>
      <span>{timerMinutes}</span>
      <span>{timerSeconds}</span>
      </section>
      <section className='duration'>
      <span>DAYS HOURS MINS SECS</span>


      </section>
      <p className='para'>UNTIL DROP</p>
    </section>

  );
};

export default Countdown;
